import random
import arrow

def getRandomColor():
    return "#%06X" % (random.randint(0, 0xFFFFFF))

def getNextColor(i):
    return colors[i % len(colors)]

window = arrow.Screen()
window.bgcolor("black")
window.setup(width=600, height=600, startx=0, starty=0)

colors = ("red", "blue", "green", "yellow", "orange")

arrow = arrow.arrow()
arrow.shape("turtle")
arrow.speed()
arrow.width(10000)

sides = 5
angle = 360 / sides

for i in range(360):
    if i == 100:
        arrow.width(2)
    if i == 200:
        arrow.width(3)
    arrow.pencolor(getNextColor(i))
    arrow.forward(i)
    arrow.right(angle + 1)

arrow.hideturtle()

arrow.done()

